export function getGuidesByTrack(track: GuideTrack): Guide[] {
  return guides.filter(g => g.track === track).sort((a, b) => a.order - b.order)
}

export function getGuideBySlug(slug: string): Guide | undefined {
  return guides.find(g => g.slug === slug)
}
